require "pp"

class Part1
  def initialize(input)
    @instructions = input
    @molecule = ""
  end

  def result
    @result_molecules = []
    @instructions.each do |instruction|
      instruction =~ /(\w+) => (\w+)/

      current_index = -1
      while current_index = @molecule.index($1, current_index + 1)
        new_molecule = @molecule.clone
        new_molecule[current_index, $1.size] = $2
        @result_molecules << new_molecule
      end
    end

    @result_molecules.uniq.count
  end
end
